<dialog style="background-color: deeppink" id="signinDialog">
  <h3>Sign in</h3>

  <form id="signinForm" method="dialog">
    <label for="email">Email</label>
    <input class="form-control" type="email" name="email">
    <label for="password">Password</label>
    <input class="form-control" type="password" name="password">
    <button style="background-color: #7a013b; color: white; border: #7a013b" class="btn btn-primary" type="submit">Sign in</button>
  </form>
</dialog>

<dialog style="background-color: deeppink" id="signupDialog">
  <h3>Add new user</h3>
  <form id="signupForm" method="dialog">
    <label for="name">Name</label>
    <input class="form-control" type="text" name="name">

    <label for="email">Email</label>
    <input class="form-control" type="email" name="email">

    <label for="password">Password</label>
    <input class="form-control" type="password" name="password">

    <label for="role">Role</label>
    <input class="form-control" type="role" name="role">

    <button style="background-color: #7a013b; color: white; border: #7a013b" class="btn btn-primary" type="submit">Sign up</button>
  </form>
</dialog>